import React, { useEffect } from 'react';
import FattMerchantApi from './apis/FattMerchantApi';
import { makeStyles } from '@material-ui/core/styles';
import MaterialTable from 'material-table';
import Grid from '@material-ui/core/Grid';
import CircularProgress from '@material-ui/core/CircularProgress';
import './App.css';
import AddIcon from '@material-ui/icons/Add';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  detailsRoot: {
    width: '100%',
    marginTop: theme.spacing(3),
    overflowX: 'auto',
  },
  table: {
    minWidth: 700,
  },
}));

const TAX_RATE = 0.07;

function ccyFormat(num) {
  return `${num.toFixed(2)}`;
}

function priceRow(qty, unit) {
  return qty * unit;
}

function createRow(desc, qty, unit) {
  const price = priceRow(qty, unit);
  return { desc, qty, unit, price };
}

function subtotal(items) {
  return items.map(({ price }) => price).reduce((sum, i) => sum + i, 0);
}

const rows = [
  createRow('Paperclips (Box)', 100, 1.15),
  createRow('Paper (Case)', 10, 45.99),
  createRow('Waste Basket', 2, 17.99),
];

const invoiceSubtotal = subtotal(rows);
const invoiceTaxes = TAX_RATE * invoiceSubtotal;
const invoiceTotal = invoiceTaxes + invoiceSubtotal;

function App() {
  const classes = useStyles();
  const fattMerchantApi = new FattMerchantApi();
  const [state, setState] = React.useState({
    data: null,
    loading: true,
    error: false,
    errorMessage: null
  })

  useEffect(() => {
    fattMerchantApi.getCatalogItems()
    .then(response => {
      setState({
        data: response,
        loading: false,
        error: false,
      })
      console.log(response)
    }).catch(error => {
      setState({
        data: null,
        loading: false,
        error: true,
        errorMessage: error
      })
    })
  }, []);

  if (state.loading) {
    return (
      <div className="App">
        <CircularProgress />
      </div>
    )
  } else if (state.error) {
    return (
      <div className="App">
        <p>An error occurred</p>
        <p>{state.errorMessage}</p>
      </div>
    )
  }
  const columns = [
    {title: "Name", field: "item"},
    {title: "Price(USD)", field: "price", type: 'numeric'},
    {title: "Stock", field: "in_stock"}
  ]
  return (
    <div className="App">
      <Grid container spacing={3}>
        <Grid item xs={6}>
          <MaterialTable
            title="Catalog"
            columns={columns}
            data={state.data.data}
            actions={[
              {
                  icon: AddIcon,
                  tooltip: 'Add Item To Invoice',
                  onClick: (event, rowData) => {
                  }
              }
          ]}
            />
        </Grid>
        <Grid item xs={5}>
          <Paper className={classes.detailsRoot}>
          <Table className={classes.table} aria-label="spanning table">
            <TableHead>
              <TableRow>
                <TableCell align="center" colSpan={3}>
                  Details
                </TableCell>
                <TableCell align="right">Price</TableCell>
              </TableRow>
              <TableRow>
                <TableCell>Desc</TableCell>
                <TableCell align="right">Qty.</TableCell>
                <TableCell align="right">Unit</TableCell>
                <TableCell align="right">Sum</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {rows.map(row => (
                <TableRow key={row.desc}>
                  <TableCell>{row.desc}</TableCell>
                  <TableCell align="right">{row.qty}</TableCell>
                  <TableCell align="right">{row.unit}</TableCell>
                  <TableCell align="right">{ccyFormat(row.price)}</TableCell>
                </TableRow>
              ))}

              <TableRow>
                <TableCell rowSpan={3} />
                <TableCell colSpan={2}>Subtotal</TableCell>
                <TableCell align="right">{ccyFormat(invoiceSubtotal)}</TableCell>
              </TableRow>
              <TableRow>
                <TableCell>Tax</TableCell>
                <TableCell align="right">{`${(TAX_RATE * 100).toFixed(0)} %`}</TableCell>
                <TableCell align="right">{ccyFormat(invoiceTaxes)}</TableCell>
              </TableRow>
              <TableRow>
                <TableCell colSpan={2}>Total</TableCell>
                <TableCell align="right">{ccyFormat(invoiceTotal)}</TableCell>
              </TableRow>
            </TableBody>
            </Table>
          </Paper>
        </Grid>
      </Grid>
    </div>
  );
}

export default App;
