import React, { useEffect } from 'react';
import FattMerchantApi from './apis/FattMerchantApi';
import { makeStyles } from '@material-ui/core/styles';
import MaterialTable from 'material-table';
import Grid from '@material-ui/core/Grid';
import CircularProgress from '@material-ui/core/CircularProgress';
import './App.css';

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
}));

function App() {

  const fattMerchantApi = new FattMerchantApi();
  const [state, setState] = React.useState({
    data: null,
    loading: true,
    error: false,
    errorMessage: null,
    columns: [
      {title: "Name", field: "item"},
      {title: "Price(USD)", field: "price", type: 'numeric'},
      {title: "Stock", field: "in_stock"}
    ]
  })

  useEffect(() => {
    fattMerchantApi.getCatalogItems()
    .then(response => {
      setState({
        data: response,
        loading: false,
        error: false,
      })
      console.log(response)
    }).catch(error => {
      setState({
        data: null,
        loading: false,
        error: true,
        errorMessage: error
      })
    })
  }, []);

  if (state.loading) {
    return (
      <div className="App">
        <CircularProgress />
      </div>
    )
  } else if (state.error) {
    return (
      <div className="App">
        <p>An error occurred</p>
        <p>{state.errorMessage}</p>
      </div>
    )
  }
  return (
    <div className="App">
      <Grid container spacing={3}>
        <Grid item xs={6}>
          <MaterialTable
            title="Catalog"
            columns={state.columns}
            data={state.data} 
            />
        </Grid>
      </Grid>
    </div>
  );
}

export default App;
